import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";

import * as actions from "../../store/actions";

import './Login.scss';
import { FormattedMessage } from 'react-intl';
import { handleLoginApi } from '../../services/userServices';


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            isShowPassword: false,
        }

    }
    handleOnchangeUsername = (event) => {
        this.setState({
            username: event.target.value
        })

    }
    handleOnchangePassword = (event) => {
        this.setState({
            password: event.target.value
        })

    }
    handleLogin = async() => {
        console.log('username:', this.state.username, 'password:', this.state.password)
        console.log('all state', this.state)
        try {

        } catch (e) {

        }
        await handleLoginApi(this.state.username, this.state.password);
    }
    handleShowHidePassword = () => {
        this.setState({
            isShowPassword: !this.state.isShowPassword
        })
    }
    render() {
        return ( <
            div className = "login-background" >
            <
            div className = "login-containers" >
            <
            div className = "login-content" >
            <
            div className = "col-12 text-login" > Login < /div> <
            div className = "col-12 form-group login-input" >
            <
            label > Username: < /label> <
            input type = "text"
            className = "form-control"
            placeholder = "Enter your username"
            value = { this.state.username }
            onChange = {
                (event) => this.handleOnchangeUsername(event) }
            /> <
            /div> <
            div className = "col-12 form-group login-input" >
            <
            label > Password: < /label> <
            div className = "custom-input-password" >
            <
            input className = "form-control"
            type = { this.state.isShowPassword ? 'text' : 'password' }
            placeholder = "Enter your password"
            onChange = {
                (event) => this.handleOnchangePassword(event) }
            />                          <
            span onClick = {
                () => this.handleShowHidePassword() } >
            < i class = { this.state.isShowPassword ? 'far fa-eye' : 'far fa-eye-slash' } > < /i></span >
            <
            /div>


            <
            /div> <
            div className = "col-12 btn-login" >
            <
            button className = "btn-login"
            onClick = {
                () => { this.handleLogin() } } > Login < /button> <
            /div>

            <
            div className = "col-12" >
            <
            span className = "forgot-password" > Forgot your password ? < /span> <
            /div> <
            div className = "col-12 text-center mt-3" >
            <
            span className = "text-other-login" > Or Login with : < /span> <
            /div> <
            div className = "col-12 social-login" >
            <
            i className = "fab fa-google-plus-g google" > < /i> <
            i className = "fab fa-facebook facebook" > < /i> <
            /div> <
            /div>

            <
            /div> <
            /div>
        )

    }
}

const mapStateToProps = state => {
    return {
        language: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) => dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
